import React, { useMemo } from 'react';
import { useForm, Controller } from 'react-hook-form';
import { Input, Button, Form, notification } from 'antd';
import { useNavigate } from 'react-router-dom';
import useWebSocket from '../../api';

type NotificationType = 'success' | 'info' | 'warning' | 'error';

interface FormData {
    username: string;
    password: string;
    confirmPassword: string;
}

const RegisterForm: React.FC = () => {
    const { control, handleSubmit } = useForm<FormData>();
    const navigate = useNavigate();
    const [api, contextHolder] = notification.useNotification();
    const { send, receivedMessages} = useWebSocket('ws://localhost:8080');

    const openNotificationWithIcon = (type: NotificationType, description?: string) => {
      api[type]({
        message: 'Notification Title',
        description: description || 'Algum erro aconteceu.',
      });
    };

    const onSubmit = (data: FormData) => {
        if (data.password !== data.confirmPassword) {
            openNotificationWithIcon('error', 'As senhas não coincidem.');
            console.error('As senhas não coincidem.');
            return;
        }

        if (!data.username || !data.password) {
            openNotificationWithIcon('error', 'Preencha todos os campos.');
            console.error('Preencha todos os campos.');
            return;
        }

         // Exemplo de envio de mensagem via WebSocketUtil
         send(`/register ${data.username} ${data.password}`);

         // Register the message handler
        handleWebSocketMessage(receivedMessages)

      };
      const handleWebSocketMessage = (message: any) => {
            console.log('Mensagem recebida:', message);
            if (message === 'success') {
              openNotificationWithIcon('success');
                navigate('/success'); // Navega para a página de sucesso
            } else if (message === 'error') {
                openNotificationWithIcon('error');
                console.error(message);
            }
        };



    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
            <div className="w-full max-w-md p-8 bg-white shadow-lg rounded-lg">
                <h2 className="text-2xl font-bold mb-6 text-center">Registro</h2>
                <Form layout="vertical" onFinish={handleSubmit(onSubmit)}>
                    <Form.Item label="Username" name="username">
                        <Controller
                            name="username"
                            control={control}
                            defaultValue=""
                            render={({ field }) => <Input {...field} placeholder="Digite seu username" />}
                        />
                    </Form.Item>

                    <Form.Item label="Password" name="password">
                        <Controller
                            name="password"
                            control={control}
                            defaultValue=""
                            render={({ field }) => <Input.Password {...field} placeholder="Digite sua senha" />}
                        />
                    </Form.Item>

                    <Form.Item label="Confirm Password" name="confirmPassword">
                        <Controller
                            name="confirmPassword"
                            control={control}
                            defaultValue=""
                            render={({ field }) => <Input.Password {...field} placeholder="Confirme sua senha" />}
                        />
                    </Form.Item>

                    <Form.Item>
                        <Button type="primary" htmlType="submit" block>
                            Register
                        </Button>
                    </Form.Item>

                    {/* Não há necessidade de mostrar errorMessage aqui */}
                </Form>
            </div>
        </div>
    );
};

export default RegisterForm;
