import { useState, useEffect, useRef, useCallback } from 'react';

const useWebSocket = (url: string) => {
  const [receivedMessages, setReceivedMessages] = useState({});
  const ws = useRef<WebSocket | null>(null);
  const reconnectTimeout = useRef<NodeJS.Timeout | null>(null);
  const reconnectAttempts = useRef(0);

  const connectWebSocket = useCallback(() => {
    if (ws.current) {
      ws.current.close();
    }

    ws.current = new WebSocket(url);

    ws.current.onopen = () => {
      console.log('Conexão WebSocket aberta');
      reconnectAttempts.current = 0; // Reset reconnection attempts on successful connection
      if (reconnectTimeout.current) {
        clearTimeout(reconnectTimeout.current);
        reconnectTimeout.current = null;
      }
    };

    ws.current.onmessage = (event) => {
      const normalizedMessage = event.data.replace(/'/g, '"');
      const parsedMessage = JSON.parse(normalizedMessage);
      if (parsedMessage !== receivedMessages)
        setReceivedMessages(parsedMessage);
    };

    ws.current.onerror = (error) => {
      console.error('Erro na conexão WebSocket:', error);
    };

    ws.current.onclose = () => {
      console.log('Conexão WebSocket fechada');
      if (!reconnectTimeout.current) {
        // Exponential backoff reconnection
        const delay = Math.min(5000 * Math.pow(2, reconnectAttempts.current), 30000); // Max 30 seconds delay
        reconnectAttempts.current += 1; // Increment attempts
        reconnectTimeout.current = setTimeout(() => {
          connectWebSocket();
        }, delay);
      }
    };
  }, [url, receivedMessages]);

  useEffect(() => {
    connectWebSocket();

    // Cleanup function to close the WebSocket and clear timeout when the component unmounts
    return () => {
      if (ws.current) {
        ws.current.close();
      }
      if (reconnectTimeout.current) {
        clearTimeout(reconnectTimeout.current);
      }
    };
  }, [connectWebSocket]);

  const send = useCallback((message: string) => {
    if (ws.current && ws.current.readyState === WebSocket.OPEN) {
      ws.current.send(message);
    }
  }, []);

  const close = useCallback(() => {
    if (ws.current) {
      ws.current.close();
    }
  }, []);

  return {
    receivedMessages,
    send,
    close
  };
};

export default useWebSocket;
