import React, { useEffect, useRef, useState } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { Controller, useForm } from 'react-hook-form';
import useWebSocket from '../../api';
import { Col, Row } from 'antd';
import { format, isToday, isYesterday, parseISO } from 'date-fns';
import { Picker } from 'emoji-mart';

// Mapeamento de emojis
const emojiMap: { [key: string]: string } = {
  ':grinning:': 'üòÅ',
  ':smile:': 'üòÑ',
  ':joy:': 'üòÇ',
  ':sob:': 'üò≠',
  // Adicione mais emojis conforme necess√°rio
};

interface ChatAreaProps {
  messages: Message[];
  selectedUser: string;
}

interface Message {
  sender: string;
  message: string;
  timestamp: string;
}

const ChatArea: React.FC<ChatAreaProps> = ({ messages, selectedUser }) => {
  const { isAuthenticated } = useAuth();
  const { control, handleSubmit, reset, watch } = useForm<{ message: string }>();
  const { send } = useWebSocket('ws://localhost:8080');

  const messagesEndRef = useRef<HTMLDivElement>(null);
  const [isScrolledUp, setIsScrolledUp] = useState(false);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);

  const handleSendMessage = (data: { message: string }) => {
    if (!data.message) return;
    send(`/sendMessage ${isAuthenticated} ${selectedUser} ${data.message}`);
    reset(); // Clear the input field after sending
    setShowEmojiPicker(false); // Hide emoji picker after sending
  };

  const addEmoji = (emoji: string) => {
    const message = watch("message", "");
    const newMessage = message + emoji;
    reset({ message: newMessage });
  };

  const message = watch("message", "");

  // Fun√ß√£o para converter strings de emoji em emojis reais
  const convertEmojiString = (text: string) => {
    return text.replace(/:\w+:/g, (match) => emojiMap[match] || match);
  };

  const formatTimestamp = (timestamp: string, prevTimestamp: string) => {
    const date = parseISO(timestamp);
    let formattedDate = '';

    if (isToday(date)) {
      formattedDate = format(date, 'HH:mm');
    } else if (isYesterday(date)) {
      if (!prevTimestamp || !isYesterday(parseISO(prevTimestamp))) {
        formattedDate = 'Ontem ' + format(date, 'HH:mm');
      } else {
        formattedDate = format(date, 'HH:mm');
      }
    } else {
      if (!prevTimestamp || format(parseISO(prevTimestamp), 'yyyy-MM-dd') !== format(date, 'yyyy-MM-dd')) {
        formattedDate = format(date, 'dd/MM/yyyy HH:mm');
      } else {
        formattedDate = format(date, 'HH:mm');
      }
    }

    return formattedDate;
  };

  const scrollToBottom = () => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  useEffect(() => {
    const container = messagesEndRef.current?.parentElement;
    if (container) {
      const handleScroll = () => {
        if (container.scrollTop + container.clientHeight < container.scrollHeight - 20) {
          setIsScrolledUp(true);
        } else {
          setIsScrolledUp(false);
        }
      };

      container.addEventListener('scroll', handleScroll);
      return () => container.removeEventListener('scroll', handleScroll);
    }
  }, [messagesEndRef]);

  useEffect(() => {
    if (!isScrolledUp) {
      scrollToBottom();
    }
  }, [messages, isScrolledUp]);

  return (
    <div className="flex flex-col w-3/4 bg-gray-100">
      <div className="flex-1 p-4 overflow-auto">
        {!!messages && messages.map((message, id) => (
          <Row key={id}>
            {message.sender === isAuthenticated && <div className='w-3/4'></div>}
            <Col
              className={`mb-2 p-2 rounded-lg w-1/4 ${message.sender === isAuthenticated ? 'bg-primary text-white self-end' : 'bg-white self-start'}`}
            >
              <div className="text-sm text-gray-600">{message.sender}</div>
              <div className="text-md">{convertEmojiString(message.message)}</div>
              <div className="text-xs text-gray-500">
                {formatTimestamp(message.timestamp, id > 0 ? messages[id - 1].timestamp || '' : '')}
              </div>
            </Col>
          </Row>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <form className="p-4 bg-white relative" onSubmit={handleSubmit(handleSendMessage)}>
        <div className="flex items-center space-x-4">
          <Controller
            name="message"
            control={control}
            defaultValue=""
            render={({ field }) => (
              <input
                {...field}
                className="flex-1 px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
                placeholder="Digite sua mensagem"
                maxLength={85}
              />
            )}
          />
          <button
            type="button"
            className="px-2 py-2 bg-gray-300 text-black rounded-lg hover:bg-gray-400 focus:outline-none focus:ring-2 focus:ring-primary"
            onClick={() => setShowEmojiPicker(!showEmojiPicker)}
          >
            üòÄ
          </button>
          <button
            type="submit"
            className="px-4 py-2 bg-primary text-white rounded-lg hover:bg-green-500 focus:outline-none focus:ring-2 focus:ring-primary"
          >
            Enviar
          </button>
        </div>
        <p className="text-sm text-gray-600 mt-2">{message.length}/85 caracteres</p>
        {showEmojiPicker && (
          <div style={{ position: 'absolute', bottom: '60px', right: '10px' }}>
            <Picker
              onSelect={(emoji: any) => addEmoji(emoji.native)}
              style={{ width: '300px' }}
            />
          </div>
        )}
      </form>
    </div>
  );
};

export default ChatArea;
