import React, { useState, useEffect, useCallback } from 'react';
import { useForm, Controller } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import useWebSocket from '../../api';
import { type NotificationInstance } from 'antd/es/notification/interface';
import { NotificationType } from '../../common/notificationType';
import { useAuth } from '../../contexts/AuthContext';
import Sidebar from '../../containers/SideBar';
import { format, isToday, isYesterday, parseISO } from 'date-fns';

interface Message {
  sender: string;
  message: string;
  timestamp: string;
}

const Home: React.FC<{ api: NotificationInstance }> = ({ api }) => {
  const { isAuthenticated } = useAuth();
  const { control, handleSubmit, reset } = useForm<{ message: string }>();
  const navigate = useNavigate();
  const { send, receivedMessages } = useWebSocket('ws://localhost:8080');
  const [friends, setFriends] = useState<string[]>()
  const [messages, setMessages] = useState<Message[]>();
  const [selectedUser, setSelectedUser] = useState<string | null>(null);

  const formatTimestamp = (timestamp: string, prevTimestamp: string) => {
    const date = parseISO(timestamp);
    let formattedDate = '';

    if (isToday(date)) {
      formattedDate = format(date, 'HH:mm');
    } else if (isYesterday(date)) {
      if (!prevTimestamp || !isYesterday(parseISO(prevTimestamp))) {
        formattedDate = 'Ontem ' + format(date, 'HH:mm');
      } else {
        formattedDate = format(date, 'HH:mm');
      }
    } else {
      if (!prevTimestamp || format(parseISO(prevTimestamp), 'yyyy-MM-dd') !== format(date, 'yyyy-MM-dd')) {
        formattedDate = format(date, 'dd/MM/yyyy HH:mm');
      } else {
        formattedDate = format(date, 'HH:mm');
      }
    }

    return formattedDate;
  };

  useEffect(() => {
    const interval = setInterval(() => {
      if (!isAuthenticated) {
        navigate('/');
      }
      send('/getAll ' + isAuthenticated);
    }, 1000);

    return () => {
      clearInterval(interval);
    };
  }, [send, navigate, isAuthenticated]);

  const openNotificationWithIcon = useCallback(
    (type: NotificationType, message: string, description: string) => {
      api[type]({
        message,
        description,
      });
    },
    [api]
  );

  const handleSendMessage = (data: { message: string }) => {
    if (!data.message) return;
    send(`/sendMessage ${isAuthenticated} ${selectedUser} ${data.message}`);
    reset();
  };

  const handleWebSocketMessage = useCallback(
    (message: string) => {

    try {
      const normalizedMessage = message.replace(/'/g, '"');
      const parsedMessage = JSON.parse(normalizedMessage);

      setFriends(Object.keys(parsedMessage));
      console.log('parsedMessage', selectedUser ? parsedMessage[selectedUser] : 'kkk ferouuu');
      if (!selectedUser) {
        return;
      }
      const newMessage: Message[] = [];
      for (const message of parsedMessage[selectedUser]) {
        newMessage.push({
          sender: message.sender,
          message: message.message,
          timestamp: message.timestamp,
        });
      }
      setMessages(newMessage);
    } catch (error) {
      console.error('Erro ao analisar a mensagem:', error);
    }
    },
    [selectedUser]
  );

  useEffect(() => {
    if (receivedMessages) {
      handleWebSocketMessage(receivedMessages);
    }
  }, [receivedMessages, handleWebSocketMessage]);

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <Sidebar 
        friends={friends} 
        openNotificationWithIcon={openNotificationWithIcon}
        selectedUser={selectedUser}
        setSelectedUser={setSelectedUser}
        />

      {/* Main Chat Area */}
      <div className="flex flex-col w-3/4 bg-gray-100">
        <div className="flex-1 p-4 overflow-auto">
          {!!messages && messages.map((message, id) => (
            <div
              key={id}
              className={`mb-2 p-2 rounded-lg ${message.sender === isAuthenticated ? 'bg-primary text-white self-end' : 'bg-white'}`}
            >
              <div className="text-sm text-gray-600">{message.sender}</div>
              <div className="text-md">{message.message}</div>
              <div className="text-xs text-gray-500">
                {formatTimestamp(message.timestamp, id > 0 ? messages[id - 1].timestamp || '' : '')}
              </div>
            </div>
          ))}
        </div>
        <form className="p-4 bg-white" onSubmit={handleSubmit(handleSendMessage)}>
          <div className="flex items-center space-x-4">
            <Controller
              name="message"
              control={control}
              defaultValue=""
              render={({ field }) => (
                <input
                  {...field}
                  className="flex-1 px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
                  placeholder="Digite sua mensagem"
                />
              )}
            />
            <button
              type="submit"
              className="px-4 py-2 bg-primary text-white rounded-lg hover:bg-green-500 focus:outline-none focus:ring-2 focus:ring-primary"
            >
              Enviar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Home;
