import React, { createContext, useContext, useState, ReactNode } from 'react';

export interface selectedUserType {
  selectedUser: string | null;
  setSelectedUser: (user: string) => void;
}

const SelectedUser = createContext<selectedUserType | null>(null);

export const useSelectedUser = () => {
  const context = useContext(SelectedUser);
  if (context === null) {
    throw new Error('useSelectedUser must be used within an SelectedUserProvider');
  }
  return context;
};

interface SelectedUserProviderProps {
  children: ReactNode;
}

export const SelectedUserProvider: React.FC<SelectedUserProviderProps> = ({ children }) => {
  const [selectedUser, setSelectedUser] = useState<string | null>(null);



  return (
    <SelectedUser.Provider value={{ selectedUser, setSelectedUser }}>
      {children}
    </SelectedUser.Provider>
  );
};
