import React from 'react';
import { useNavigate } from 'react-router-dom';

interface Friend {
  id: number;
  name: string;
}

interface SidebarProps {
  friends: Friend[];
  getFriends: () => void;
}

const Sidebar: React.FC<SidebarProps> = ({ friends, getFriends }) => {
  const navigate = useNavigate();
  return (
    <div className="w-1/4 bg-lightGray p-4">
      {/* <h2 className="text-2xl font-bold mb-4">Amigos</h2>
      <ul className="space-y-2">
        {friends.map((friend) => (
          <li key={friend.id} className="p-2 bg-white rounded-lg shadow">
            {friend.name}
          </li>
        ))}
      </ul> */}
      <button
        className="mt-4 w-full px-4 py-2 bg-primary text-white rounded-lg hover:bg-green-500 focus:outline-none focus:ring-2 focus:ring-primary"
        onClick={() => getFriends()}
      >
        Adicionar Amigo
      </button>
      <input
        type="text"
        placeholder="Username"
        className="mt-4 w-full px-4 py-2 bg-white rounded-lg shadow"
      />
      <button
        className="mt-4 w-full px-4 py-2 bg-primary text-white rounded-lg hover:bg-green-500 focus:outline-none focus:ring-2 focus:ring-primary"
        onClick={() => {
          const username = document.querySelector('input')?.value;
          if (username) {
            if (!username) {
              alert('Digite um username');
            }

          }
        }}
      >
        Adicionar Amigo
      </button>
    </div>
  );
};

export default Sidebar;
