// src/components/RegisterForm.tsx

import React, { useState, useEffect } from 'react';
import { useForm, Controller } from 'react-hook-form';
import { Input, Button, Form, Alert } from 'antd';
import { useNavigate } from 'react-router-dom';
import 'tailwindcss/tailwind.css';
import WebSocketClient from '../../api';

interface FormData {
  username: string;
  password: string;
  confirmPassword: string;
}

const RegisterForm: React.FC = () => {
  const { control, handleSubmit } = useForm<FormData>();
  const [errorMessage, setErrorMessage] = useState<string | null>(null);
  const navigate = useNavigate();

  // Create a WebSocketClient instance
  const webSocketClient = new WebSocketClient('ws://localhost:8080');

  useEffect(() => {
    const handleWebSocketMessage = (message: any) => {
      if (message.status === 'success') {
        navigate('/success'); // Navega para a página de sucesso
      } else if (message.status === 'error') {
        setErrorMessage(message.message);
      }
    };

    // Register the message handler
    webSocketClient.onMessage(handleWebSocketMessage);

    // Clean up WebSocket connection on component unmount
    return () => {
      webSocketClient.close();
    };
  }, [navigate, webSocketClient]);

  const onSubmit = (data: FormData) => {
    if (data.password !== data.confirmPassword) {
      setErrorMessage('As senhas não coincidem.');
      return;
    }

    // Send WebSocket message
    webSocketClient.send({
      endpoint: '/register',
      message: [data.username, data.password],
    });
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md p-8 bg-white shadow-lg rounded-lg">
        <h2 className="text-2xl font-bold mb-6 text-center">Registro</h2>
        <Form layout="vertical" onFinish={handleSubmit(onSubmit)}>
          <Form.Item label="Username" name="username">
            <Controller
              name="username"
              control={control}
              defaultValue=""
              render={({ field }) => <Input {...field} placeholder="Digite seu username" />}
            />
          </Form.Item>

          <Form.Item label="Password" name="password">
            <Controller
              name="password"
              control={control}
              defaultValue=""
              render={({ field }) => <Input.Password {...field} placeholder="Digite sua senha" />}
            />
          </Form.Item>

          <Form.Item label="Confirm Password" name="confirmPassword">
            <Controller
              name="confirmPassword"
              control={control}
              defaultValue=""
              render={({ field }) => <Input.Password {...field} placeholder="Confirme sua senha" />}
            />
          </Form.Item>

          <Form.Item>
            <Button type="primary" htmlType="submit" block>
              Register
            </Button>
          </Form.Item>

          {errorMessage && (
            <Form.Item>
              <Alert message={errorMessage} type="error" showIcon />
            </Form.Item>
          )}
        </Form>
      </div>
    </div>
  );
};

export default RegisterForm;
