import React, { useState } from 'react';
import { useForm, Controller } from 'react-hook-form';
import { Input, Button, Form, Alert } from 'antd';
import { useNavigate } from 'react-router-dom';
import 'tailwindcss/tailwind.css';

interface FormData {
  username: string;
  password: string;
  confirmPassword: string;
}

const RegisterForm: React.FC = () => {
  const { control, handleSubmit, watch } = useForm<FormData>();
  const [errorMessage, setErrorMessage] = useState<string | null>(null);
  const navigate = useNavigate();
  const watchPassword = watch('password');

  const onSubmit = async (data: FormData) => {
    if (data.password !== data.confirmPassword) {
      setErrorMessage('As senhas não coincidem.');
      return;
    }

    try {
      const response = await fetch('ws://localhost:8080', {
        method: 'POST',
        body: JSON.stringify({
          endpoint: '/register',
          message: [data.username, data.password],
        }),
      });

      console.log(result);

      const result = await response.json();



      if (result.status === 'success') {
        navigate('/success'); // Navega para a página de sucesso
      } else if (result.status === 'error') {
        setErrorMessage(result.message);
      }
    } catch (error) {
      setErrorMessage('Erro ao conectar com o servidor');
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md p-8 bg-white shadow-lg rounded-lg">
        <h2 className="text-2xl font-bold mb-6 text-center">Registro</h2>
        <Form layout="vertical" onFinish={handleSubmit(onSubmit)}>
          <Form.Item label="Username" name="username">
            <Controller
              name="username"
              control={control}
              defaultValue=""
              render={({ field }) => <Input {...field} placeholder="Digite seu username" />}
            />
          </Form.Item>

          <Form.Item label="Password" name="password">
            <Controller
              name="password"
              control={control}
              defaultValue=""
              render={({ field }) => <Input.Password {...field} placeholder="Digite sua senha" />}
            />
          </Form.Item>

          <Form.Item label="Confirm Password" name="confirmPassword">
            <Controller
              name="confirmPassword"
              control={control}
              defaultValue=""
              render={({ field }) => <Input.Password {...field} placeholder="Confirme sua senha" />}
            />
          </Form.Item>

          <Form.Item>
            <Button type="primary" htmlType="submit" block>
              Register
            </Button>
          </Form.Item>

          {errorMessage && (
            <Form.Item>
              <Alert message={errorMessage} type="error" showIcon />
            </Form.Item>
          )}
        </Form>
      </div>
    </div>
  );
};

export default RegisterForm;
