import React, { useState, useEffect, useCallback } from 'react';
import { useForm, Controller } from 'react-hook-form';
import { useNavigate } from 'react-router-dom';
import useWebSocket from '../../api';
import { type NotificationInstance } from 'antd/es/notification/interface';
import { NotificationType } from '../../common/notificationType';
import { useAuth } from '../../contexts/AuthContext';
import Sidebar from '../../containers/SideBar';

interface Friend {
  id: number;
  name: string;
}

interface Message {
  id: number;
  sender: string;
  content: string;
  timestamp: string;
}

const Home: React.FC<{ api: NotificationInstance }> = ({ api }) => {
  const { isAuthenticated } = useAuth();
  const { control, handleSubmit, reset } = useForm<{ message: string }>();
  const navigate = useNavigate();
  const { send, receivedMessages } = useWebSocket('ws://localhost:8080');
  const [friends, setFriends] = useState<Friend[]>([
    { id: 1, name: 'John Doe' },
    { id: 2, name: 'Jane Smith' },
  ]);
  const [messages, setMessages] = useState<Message[]>([
    { id: 1, sender: 'John Doe', content: 'Hello!', timestamp: '12:00' },
    { id: 2, sender: 'You', content: 'Hi, how are you?', timestamp: '12:01' },
  ]);

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/');
    }
    send('/getUsernameNewMsgs ' + isAuthenticated);
  }, [send, navigate, isAuthenticated]);


  const openNotificationWithIcon = useCallback(
    (type: NotificationType, message: string, description: string) => {
      api[type]({
        message,
        description,
      });
    },
    [api]
  );

  const handleSendMessage = (data: { message: string }) => {
    if (!data.message) return;
    const newMessage: Message = {
      id: messages.length + 1,
      sender: 'You',
      content: data.message,
      timestamp: new Date().toLocaleTimeString(),
    };
    send(`/sendMessage ${data.message}`);
    reset();
  };

  const handleWebSocketMessage = useCallback(
    (message: any) => {
      console.log('Mensagem recebidaaaa:', message);
      setFriends(message);

    },
    [messages]
  );

  useEffect(() => {
    if (receivedMessages) {
      handleWebSocketMessage(receivedMessages);
    }
  }, [receivedMessages, handleWebSocketMessage]);

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <Sidebar friends={friends} />

      {/* Main Chat Area */}
      <div className="flex flex-col w-3/4 bg-gray-100">
        <div className="flex-1 p-4 overflow-auto">
          {messages.map((message) => (
            <div
              key={message.id}
              className={`mb-2 p-2 rounded-lg ${message.sender === 'You' ? 'bg-primary text-white self-end' : 'bg-white'}`}
            >
              <div className="text-sm text-gray-600">{message.sender}</div>
              <div className="text-md">{message.content}</div>
              <div className="text-xs text-gray-500">{message.timestamp}</div>
            </div>
          ))}
        </div>
        <form className="p-4 bg-white" onSubmit={handleSubmit(handleSendMessage)}>
          <div className="flex items-center space-x-4">
            <Controller
              name="message"
              control={control}
              defaultValue=""
              render={({ field }) => (
                <input
                  {...field}
                  className="flex-1 px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary"
                  placeholder="Digite sua mensagem"
                />
              )}
            />
            <button
              type="submit"
              className="px-4 py-2 bg-primary text-white rounded-lg hover:bg-green-500 focus:outline-none focus:ring-2 focus:ring-primary"
            >
              Enviar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Home;
