import React, { useCallback, useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiLogOut } from 'react-icons/fi';
import { NotificationType } from '../../common/notificationType';
import useWebSocket from '../../api';
import { useAuth } from '../../contexts/AuthContext';
import { Button } from 'antd';

interface SidebarProps {
  friends: string[] | undefined;
  setSelectedUser: (username: string) => void;
  selectedUser: string | null;
  openNotificationWithIcon: (type: NotificationType, message: string, description: string) => void;
}

const Sidebar: React.FC<SidebarProps> = ({ friends, setSelectedUser, selectedUser, openNotificationWithIcon }) => {
  const navigate = useNavigate();
  const { send, receivedMessages } = useWebSocket('ws://localhost:8080');
  const { isAuthenticated, logout } = useAuth();
  const inputRef = useRef<HTMLInputElement>(null);
  const [newMessages, setNewMessages] = useState<Set<string>>(new Set());

  const handleAddFriend = (username: string) => {
    send(`/addFriend ${isAuthenticated} ${username}`);
  };

  const handleChangeFriend = (username: string) => {
    console.log('Mudando de amigo:', username);
    setSelectedUser(username);
    setNewMessages((prev) => {
      const newSet = new Set(prev);
      newSet.delete(username);
      return newSet;
    });
  }

  const handleWebSocketMessage = useCallback(
    (message: any) => {
      console.log('Mensagem recebidaaaa:', message);
      if (message === 'success') {
        openNotificationWithIcon('success', 'Amigo Adicionado', 'Amigo adicionado com sucesso!');
      } else if (message === 'error') {
        openNotificationWithIcon('error', 'Erro', 'Usuário não encontrado.');
      } else {
        openNotificationWithIcon('error', 'Erro', 'Erro ao adicionar amigo.');
      }
    },
    [openNotificationWithIcon]
  );

  useEffect(() => {
    if (receivedMessages) {
      handleWebSocketMessage(receivedMessages);
    }
  }, [receivedMessages, handleWebSocketMessage]);

  return (
    <div className="flex flex-col h-full bg-lightGray p-4">
      <div className="flex-grow">
        <div className="flex items-center justify-between">
          <h2 className="text-2xl font-bold mb-4">Amigos</h2>
          <FiLogOut
            className="text-2xl cursor-pointer"
            onClick={() => {
              logout();
              navigate('/');
            }}
          />
        </div>
        <ul className="space-y-2">
          {!!friends && friends.map((friend) => (
            <Button
              key={friend}
              className={`w-full text-left ${selectedUser === friend ? 'bg-primary text-white' : 'bg-white'} ${newMessages.has(friend) ? 'bg-yellow-300' : ''}`}
              onClick={() => handleChangeFriend(friend)}
            >
              {friend}
            </Button>
          ))}
        </ul>
      </div>
      <div className="mt-4">
        <input
          type="text"
          placeholder="Nome do amigo"
          className="w-full px-4 py-2 bg-white rounded-lg shadow mb-2"
          ref={inputRef}
        />
        <button
          className="w-full px-4 py-2 bg-primary text-white rounded-lg hover:bg-green-500 focus:outline-none focus:ring-2 focus:ring-primary"
          onClick={() => {
            const username = inputRef.current?.value.trim();
            if (username) {
              handleAddFriend(username);
            } else {
              openNotificationWithIcon('error', 'Erro', 'Digite um nome de usuário.');
            }
          }}
        >
          Adicionar Amigo
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
