import React from 'react';
import { useForm, Controller } from 'react-hook-form';
import { Input, Button, Form, Alert } from 'antd';
import { useNavigate } from 'react-router-dom';
import 'tailwindcss/tailwind.css';

interface FormData {
  username: string;
  password: string;
  confirmPassword: string;
}

const RegisterForm: React.FC = () => {
  const { control, handleSubmit, watch, setError } = useForm<FormData>();
  const navigate = useNavigate();
  const watchPassword = watch('password');

  const onSubmit = async (data: FormData) => {
    try {
      const response = await fetch('ws://localhost:8080', {
        method: 'POST',
        body: JSON.stringify({
          endpoint: '/register',
          message: [data.username, data.password],
        }),
      });

      const result = await response.json();

      if (result.status === 'success') {
        navigate('/success'); // Navega para a página de sucesso
      } else if (result.status === 'error') {
        setError('root', { type: 'manual', message: result.message });
      }
    } catch (error) {
      setError('root', { type: 'manual', message: 'Erro ao conectar com o servidor' });
    }
  };

  return (
    <div className="flex h-50 items-center justify-center min-h-screen bg-gray-100">
      <div className="w-full max-w-md p-8 bg-white shadow-lg rounded-lg">
        <h2 className="text-2xl font-bold mb-6 text-center">Registro</h2>
        <Form layout="vertical" onFinish={handleSubmit(onSubmit)}>
          <Form.Item label="Username" name="username">
            <Controller
              name="username"
              control={control}
              defaultValue=""
              render={({ field }) => <Input {...field} placeholder="Digite seu username" />}
            />
          </Form.Item>

          <Form.Item label="Password" name="password">
            <Controller
              name="password"
              control={control}
              defaultValue=""
              render={({ field }) => <Input.Password {...field} placeholder="Digite sua senha" />}
            />
          </Form.Item>

          <Form.Item label="Confirm Password" name="confirmPassword">
            <Controller
              name="confirmPassword"
              control={control}
              defaultValue=""
              render={({ field }) => <Input.Password {...field} placeholder="Confirme sua senha" />}
            />
          </Form.Item>

          {watchPassword && (
            <Form.Item>
              <Button type="primary" htmlType="submit" block>
                Register
              </Button>
            </Form.Item>
          )}

          <Controller
            name="username"
            control={control}
            render={({ field }) =>{
              return (
                field?.value ? (
                  <Alert message={field.value} type="error" showIcon />
                ) : <></>
              )
            }

            }
          />
        </Form>
      </div>
    </div>
  );
};

export default RegisterForm;
